<div class="row">
    <div class="col-12 col-lg-3">
        <div class="form-group">
            <label>FAMILY NAME:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-faname" name="item-a-family-name" required>
            </div>
        </div>

    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>FIRST NAME:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-fname" name="item-a-first-name" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>MIDDLE NAME:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-mname" name="item-a-middle-name" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>QUALIFIER:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-qual" name="item-a-qualifier" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>NICKNAME:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-nname" name="item-a-nickname" required>
            </div>
        </div>
    </div>
</div>
<hr>

<div class="row">
    <div class="col-12 col-lg-2">
        <div class="form-group">
            <label>CITIZENSHIP:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-cship" name="item-a-citizenship" required>
            </div>
        </div>

    </div>
    <div class="col-lg-1">
        <div class="form-group">
            <label>SEX:</label>
            <div class="input-group mb-3">
                <select class="form-control input-item-a input-item-a-gender" name="item-a-gender" required>
                <option selected disabled value="">Gender</option>
                    <option value="1">Male</option>
                    <option value="2">Female</option>
                </select>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>CIVIL STATUS:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-cstats" name="item-a-civil-status" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>DATE OF BIRTH:</label>
            <div class="input-group mb-3">
                <input type="date" class="form-control  input-item-a  input-item-a-bdate"  name="item-a-birthday"  required>
            </div>
        </div>
    </div>
    <div class="col-lg-1">
        <div class="form-group">
            <label>AGE:</label>
            <div class="input-group mb-3">
                <input type="number" class="form-control input-item-a input-item-a-age" min="10" max="100" name="item-a-age" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>PLACE OF BIRTH:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-place-bday" name="item-a-pbd" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>HOME PHONE:</label>
            <div class="input-group mb-3">
                <input type="Tel" placeholder="Format: 123-456-7890" title="Telephone number should be separated by dash" class="form-control input-item-a input-item-a-home-num" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
       required name="item-a-home-phone" required>
            </div>
        </div>
    </div>
    <label>MOBILE PHONE:</label>
    <div class="col-lg-2">
        <div class="input-group form-group " style="position: relative;">
            <input type="tel" class="form-control phone-a phone input-item-a input-item-a-mobile-num" name="item-a-mobile-phone" pattern="[0-9]{3}[0-9]{3}[0-9]{4}" placeholder="" required>
            <span class="countrycode2" >
                +63 </span>
            <div  class=" phone-icon input-group-append">
                <div class="input-group-text">
                    <span class="fas fa-phone"></span>
                </div>
            </div>
        </div>
    </div>
</div>
<hr>
<div class="row">
    <div class="col-12 col-lg-3">
        <div class="form-group">
            <label>CURRENT ADDRESS(STREET):</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-curr-addr" name="item-a-current-addrs" required>
            </div>
        </div>

    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>VILLAGE SITIO:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-sit" name="item-a-sitio" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>BARANGAY:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a  input-item-a-brgy" name="item-a-barangay" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>TOWN/CITY:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-city" name="item-a-city" required>
            </div>

        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>PROVINCE:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-prov" name="item-a-province"required >
            </div>
        </div>
    </div>

</div>
<hr>


<div class="row other-address-item-a">
    <div class="col-12 col-lg-3">
        <div class="form-group">
            <label>OTHER ADDRESS(STREET):</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-oth-addr" name="item-a-other-addrs" required>
            </div>
        </div>

    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>VILLAGE SITIO:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-sit2" name="item-a-sitio2" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>BARANGAY:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-brgy2" name="item-a-barangay2" required>
            </div>
        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>TOWN/CITY:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-city2" name="item-a-city2" required>
            </div>

        </div>
    </div>
    <div class="col-lg-2">
        <div class="form-group">
            <label>PROVINCE:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-prov2" name="item-a-province2" required>
            </div>
        </div>
    </div>
    <hr>
</div>



<div class="row">
    <div class="col-12 col-lg-3">
        <div class="form-group">
            <label>HIGHEST EDUCATIONAL ATTAINMENT:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-educ" name="item-a-educ" required>
            </div>
        </div>

    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>OCCUPATION:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-occ" name="item-a-work" required>
            </div>
        </div>
    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>ID CARD PRESENTED:</label>
            <div class="input-group mb-3">
                <input type="text" class="form-control input-item-a input-item-a-id-press" name="item-a-id-bought" required>
            </div>
        </div>
    </div>
    <div class="col-lg-3">
        <div class="form-group">
            <label>EMAIL ADDRESS:</label>
            <div class="input-group mb-3">
                <input type="email" class="form-control input-item-a input-item-a-email" name="item-a-email" required>
            </div>

        </div>
    </div>
</div>